import{_ as e,c as r,an as o,o as a}from"./chunks/framework.CkyLdKpM.js";const s=JSON.parse('{"title":"TCP和UDP的区别","description":"","frontmatter":{},"headers":[],"relativePath":"计算机网络相关/TCP和UDP的区别.md","filePath":"计算机网络相关/TCP和UDP的区别.md","lastUpdated":1634152441000}'),l={name:"计算机网络相关/TCP和UDP的区别.md"};function i(d,t,n,P,c,h){return a(),r("div",null,t[0]||(t[0]=[o('<h1 id="tcp和udp的区别" tabindex="-1">TCP和UDP的区别 <a class="header-anchor" href="#tcp和udp的区别" aria-label="Permalink to &quot;TCP和UDP的区别&quot;">​</a></h1><h2 id="_1-对比" tabindex="-1">1. 对比 <a class="header-anchor" href="#_1-对比" aria-label="Permalink to &quot;1. 对比&quot;">​</a></h2><table tabindex="0"><thead><tr><th></th><th>UDP</th><th>TCP</th></tr></thead><tbody><tr><td>是否连接</td><td>无连接</td><td>面向连接</td></tr><tr><td>是否可靠</td><td>不可靠传输，不使用流量控制和拥塞控制</td><td>可靠传输，使用流量控制和拥塞控制</td></tr><tr><td>连接对象个数</td><td>支持一对一，一对多，多对一和多对多交互通信</td><td>只能是一对一通信</td></tr><tr><td>传输方式</td><td>面向报文</td><td>面向字节流</td></tr><tr><td>首部开销</td><td>首部开销小，仅8字节</td><td>首部最小20字节，最大60字节</td></tr><tr><td>适用场景</td><td>适用于实时应用（IP电话、视频会议、直播等）</td><td>适用于要求可靠传输的应用，例如文件传输</td></tr></tbody></table><h2 id="_2-应用场景" tabindex="-1">2.应用场景 <a class="header-anchor" href="#_2-应用场景" aria-label="Permalink to &quot;2.应用场景&quot;">​</a></h2><p><strong>UDP</strong>的应用场景：即时通信。面向数据报方式；网络数据大多为短消息；拥有大量客户端；对数据安全性无特殊要求；网络负担重但对响应速度要求高的场景。eg: IP电话、实时视频会议等。</p><p><strong>TCP</strong>的应用场景：对数据准确性要求高，速度可以相对较慢的。eg: 文件传输、邮件的发送与接收等。</p><h2 id="_3-运行在tcp-或udp的应用层协议分析" tabindex="-1">3.运行在TCP 或UDP的应用层协议分析 <a class="header-anchor" href="#_3-运行在tcp-或udp的应用层协议分析" aria-label="Permalink to &quot;3.运行在TCP 或UDP的应用层协议分析&quot;">​</a></h2><p>运行在TCP协议上的协议：</p><ul><li>HTTP（Hypertext Transfer Protocol，超文本传输协议），主要用于普通浏览。</li><li>HTTPS（HTTP over SSL，安全超文本传输协议）,HTTP协议的安全版本。</li><li>FTP（File Transfer Protocol，文件传输协议），用于文件传输。</li><li>POP3（Post Office Protocol, version 3，邮局协议），收邮件用。</li><li>SMTP（Simple Mail Transfer Protocol，简单邮件传输协议），用来发送电子邮件。</li><li>TELNET（Teletype over the Network，网络电传），通过一个终端（terminal）登陆到网络。</li><li>SSH（Secure Shell，用于替代安全性差的TELNET），用于加密安全登陆用。</li></ul><p>运行在UDP协议上的协议：</p><ul><li>BOOTP（Boot Protocol，启动协议），应用于无盘设备。</li><li>NTP（Network Time Protocol，网络时间协议），用于网络同步。</li><li>DHCP（Dynamic Host Configuration Protocol，动态主机配置协议），动态配置IP地址。</li></ul><p>运行在TCP和UDP协议上：</p><ul><li>DNS（Domain Name Service，域名服务），用于完成地址查找，邮件转发等工作。</li><li>ECHO（Echo Protocol，回绕协议），用于查错及测量应答时间（运行在<a href="https://link.juejin.cn?target=http%3A%2F%2Fzh.wikipedia.org%2Fzh-cn%2FTCP" target="_blank" rel="noreferrer">TCP</a>和<a href="https://link.juejin.cn?target=http%3A%2F%2Fzh.wikipedia.org%2Fzh-cn%2FUDP" target="_blank" rel="noreferrer">UDP</a>协议上）。</li><li>SNMP（Simple Network Management Protocol，简单网络管理协议），用于网络信息的收集和网络管理。</li><li>DHCP（Dynamic Host Configuration Protocol，动态主机配置协议），动态配置IP地址。</li><li>ARP（Address Resolution Protocol，地址解析协议），用于动态解析以太网硬件的地址。</li></ul><p>参考：<a href="https://juejin.cn/post/6992743999756845087" target="_blank" rel="noreferrer">https://juejin.cn/post/6992743999756845087</a></p>',14)]))}const T=e(l,[["render",i]]);export{s as __pageData,T as default};
